    "fields": {
        "project": {
            "key": "{{jiraKey}}"
        },
        "parent": {
            "key": "{{key}}"
        },
        "summary": "Turn Off Jira Labs View",

          "description": {
            "version": 1,
            "type": "doc",
            "content": [
                {
                    "type": "heading",
                    "attrs": {
                        "level": 1
                    },
                    "content": [
                        {
                            "type": "text",
                            "text": "Summary:"
                        }
                    ]
                },
                {
                    "type": "paragraph",
                    "content": [
                        {
                            "type": "text",
                            "text": "Jira offers some different views for navigation.  We are going to toggle to a different view that makes it easier to update your ticket for tracking.  You can always flip the view back, so no need to be nervous."
                        }
                    ]
                },
                {
                    "type": "heading",
                    "attrs": {
                        "level": 1
                    },
                    "content": [
                        {
                            "type": "text",
                            "text": "Actions to Complete:"
                        }
                    ]
                },
                {
                    "type": "orderedList",
                    "content": [
                        {
                            "type": "listItem",
                            "content": [
                                {
                                    "type": "paragraph",
                                    "content": [
                                        {
                                            "type": "text",
                                            "text": "Go to this link "
                                        },
                                        {
                                            "type": "text",
                                            "text": "Switch View Setting",
                                            "marks": [
                                                {
                                                    "type": "link",
                                                    "attrs": {
                                                        "href": "https://upstartnetwork.atlassian.net/secure/ViewPersonalSettings.jspa"
                                                    }
                                                }
                                            ]
                                        },
                                        {
                                            "type": "text",
                                            "text": ". Open in a new tab so you can keep this issue open."
                                        }
                                    ]
                                }
                            ]
                        },
                        {
                            "type": "listItem",
                            "content": [
                                {
                                    "type": "paragraph",
                                    "content": [
                                        {
                                            "type": "text",
                                            "text": "Scroll down to Toggle the "
                                        },
                                        {
                                            "type": "text",
                                            "text": "Jira Labs View",
                                            "marks": [
                                                {
                                                    "type": "em"
                                                },
                                                {
                                                    "type": "strong"
                                                }
                                            ]
                                        },
                                        {
                                            "type": "text",
                                            "text": " to â€œoffâ€ and get through the prompt to make sure the toggle is â€œGrayâ€ (set to off).  "
                                        }
                                    ]
                                }
                            ]
                        },
                        {
                            "type": "listItem",
                            "content": [
                                {
                                    "type": "paragraph",
                                    "content": [
                                        {
                                            "type": "text",
                                            "text": "Take a screenshot of updated setting and attach it to this ticket, so you can confirm this was completed."
                                        }
                                    ]
                                }
                            ]
                        },
                        {
                            "type": "listItem",
                            "content": [
                                {
                                    "type": "paragraph",
                                    "content": [
                                        {
                                            "type": "text",
                                            "text": "Close the setting tab, and from this issue \"Refresh\" your browser so you can see different view."
                                        }
                                    ]
                                }
                            ]
                        },
                        {
                            "type": "listItem",
                            "content": [
                                {
                                    "type": "paragraph",
                                    "content": [
                                        {
                                            "type": "text",
                                            "text": "Mark this sub-task as â€œDoneâ€ (notice position has changed slightly)."
                                        }
                                    ]
                                }
                            ]
                        }
                    ]
                },
                {
                    "type": "heading",
                    "attrs": {
                        "level": 1
                    },
                    "content": [
                        {
                            "type": "text",
                            "text": "Video Guide:"
                        }
                    ]
                },
